mapper.select:
SELECT `count`, `status`, version, owner_id, price, `name`, trade_location, preview_image
FROM commodity
WHERE commodity_id = #{id}

mapper.update:
UPDATE commodity

    name=#{commodity.name}
    price=#{commodity.price}
    description=#{commodity.description}
    tradeLocation=#{commodity.tradeLocation}

WHERE commodity_id = #{commodity.commodityId} AND owner_id = #{ownerId}
